import React, {PropTypes} from 'react'

export default class RelayContext {
  static propTypes = {
    relay: PropTypes.object.isRequired,
    children: PropTypes.node
  }

  static childContextTypes = {
    relay: PropTypes.object.isRequired
  }

  getChildContext() {
    return {
      relay: this.props.relay
    }
  }

  render() {
    return (
      <div>
        {this.props.children}
      </div>
    )
  }
}
