export default class Mutation {
  constructor() {
    this._response = undefined
    this._error = undefined
  }

  setResponse(response) {
    this._response = response
  }

  getResponse() {
    return this._response
  }

  setError(error) {
    this._error = error
  }

  getError() {
    return this._error
  }

  getOptimisticReferences() {
    throw new Error('Not implemented')
  }

  getReferences() {
    throw new Error('Not implemented')
  }

  isAffected() {
    return false
  }
}
